import logo from './logo.svg';
import './App.css';
// import Recipe from './client/components/Recipe/Recipe';
import Home from './client/components/Home/Home';
import './client/components/style.scss';
import { useEffect, useState } from 'react';
import Banner from './client/components/Banner/Banner';
import { Route, Routes, useLocation, Navigate } from 'react-router-dom';
import Login from './client/components/Login/Login';
import history from './client/history';
import PRoutes from './client/PRoutes';
import { createContext } from "react";

export const UserContext = createContext();

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false)
  const [user, setUser] = useState({ loggedIn: false });
    // fetching the GET route from the Express server which matches the GET route from server.js
  // async function callBackendAPI () {
  //   const response = await fetch('/users');
  //   const body = await response.json();
  //   if (response.status !== 200) {
  //     throw Error(body.message) 
  //   }
  //   return body;
  // }
  let location = useLocation();
  useEffect(() => {
    const sessionId = window.localStorage.getItem('session')

    fetch('/sessions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({uuid: sessionId})
    }).then(response => response.json())
      .then(data => {
        console.log(data, 'ffffff')
        if(data.status == 403) {
          setIsLoggedIn(false)
          history.push('/')
        } else {
          setIsLoggedIn(true)
          history.push('/home')
        }
      });
    // if(sessionId) {

    // }
    // callBackendAPI()
    //   .then(res => console.log(res, 'hellloooooo'))
    //   .catch(err => console.log(err));
  }, [])

  return <UserContext.Provider value={{ user, setUser }}>
      <Banner />
      <Routes>
        {/* {isLoggedIn && <Navigate to={{ pathname: '/'}} />} */}
        <Route path="/" element={<Login isLoggedIn={isLoggedIn}/>}/>
        <Route element={<PRoutes isLoggedIn={isLoggedIn}/>}>
          <Route path="/home" element={<Home />} exact/>
        </Route>
      </Routes>
    </UserContext.Provider>
}

export default App;
