import './card.scss'
import { RiCloseCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';
import { useRef, useState } from 'react';
import AddRecipeModal from '../AddRecipeModal/AddRecipeModal';
import ConfirmationModal from '../ConfirmationModal/ConfirmationModal';


function Card(props) {
    const {data} = props
    const recipeRef = useRef(null);
    const confirmationRef = useRef(null);
  //   return <div class="card">
  //   <div class="container">
  //     <h4 onClick={()=> {}}><b>{data.name}</b></h4> 
  //   </div>
  // </div>


  const removeRecipe = name => {
    recipeRef.current.style.display = "block";
    // const removedArr = [...recipes].filter(recipe => recipe.id !== id);

    // setRecipes(removedArr);
  };

  const editRecipe = name => {
    confirmationRef.current.style.display = "block";
    // const removedArr = [...recipes].filter(recipe => recipe.id !== id);

    // setRecipes(removedArr);
  };


  const edit = e => {
    recipeRef.current.style.display = "block";
  };

  return <div className='cardIcons'><div className='card' key={data.name}>
    <div className="container">
      <h4 onClick={()=> {}}><b>{data.name}</b></h4> 
    </div>
    </div>
    <div className='icons'>
        <RiCloseCircleLine
          onClick={removeRecipe}
          className='delete-icon'
        />
        <TiEdit
          onClick={editRecipe}
          className='edit-icon'
        />
    </div>
    {/* <ConfirmationModal /> */}
    <ConfirmationModal ref={confirmationRef} data={data} />
    <ConfirmationModal ref={recipeRef} data={data} />
  </div>
}

export default Card;
