import { Component } from "react";
import $ from 'jquery'

class Recipe extends Component {
    constructor(props) {
		super(props);

		this.open = this.open.bind(this);
		this.onNameChange = this.onNameChange.bind(this);
		this.onIngredientsChange = this.onIngredientsChange.bind(this);
		this.onPhotoChange = this.onPhotoChange.bind(this);
	}

	state = {
		name: this.props.recipe.name,
		ingredients: this.props.recipe.ingredients,
		photo: this.props.recipe.photo
	};

	open() {
		$('#' + this.props.recipe.modalId).openModal({dismissible: false});
	}

	onNameChange(e) {
		this.setState({
			name: e.target.value
		});

	}
	onIngredientsChange(e) {
		this.setState({
			ingredients: e.target.value.split(',')
		});

	}
	onPhotoChange(e) {
		this.setState({
			photo: e.target.value
		});

	}
    render() {
        let idx = 0;
		let ingredients = this.state.ingredients.map((ingredient, idx) => {
			return <IngredientComponent key={ingredient} ingredient={ingredient} id={idx + 1} />
		});
        return (
			<li>
					<div className="collapsible-header">{this.state.name}</div>
					<div className="collapsible-body">
						<div className="row nomar">
							<div className="col m6 s8 offset-s2 nopad">
								<img src={this.state.photo} alt="" className="recipe-img responsive-img" />
							</div>
							<div className="col m6">
								<h4>Ingredients</h4>
								<ul className="ingredients">
									{ingredients}
								</ul>

								<a onClick={this.open} className="waves-effect waves-light yellow darken-2 btn modal-trigger" href={'#' + this.props.recipe.modalId}>Edit</a>

								<div id={this.props.recipe.modalId} className="modal">
									<div className="modal-content">
										<h4>Modal Header</h4>
										<div className="input-field">
											<input onChange={this.onNameChange} id="name" type="text" value={this.state.name} className="validate"/>
											<label htmlFor="name">Name</label>
										</div>
										<div className="input-field">
											<input onChange={this.onIngredientsChange} id="ingredients" type="text" value={this.state.ingredients} className="validate"/>
											<label htmlFor="ingredients">Ingredients</label>
										</div>
										<div className="input-field">
											<input onChange={this.onPhotoChange} id="photoUrl" type="text" value={this.state.photo} className="validate"/>
											<label htmlFor="photoUrl">Photo</label>
										</div>
									</div>
									<div className="modal-footer">
										<a href="#!" className=" modal-action modal-close waves-effect waves-green btn-flat">Agree</a>
									</div>
								</div>
								<a id={--this.props.id} onClick={this.props.deleteRecipe} className="waves-effect waves-light  red darken-2 delete btn">DELETE</a>
							</div>
						</div>
					</div>
				</li>
		)
    }
}


class IngredientComponent extends React.Component {
	render() {
		return (
			<li>{this.props.ingredient}</li>
		)
	}
}

export default Recipe
