import React, { useState, useEffect, useRef, createRef } from 'react';
const AddRecipeModal = React.forwardRef(function AddRecipeModal(props, addRef) {

    const onModalCancel = e => {
        addRef.current.style.display = "none";
    }

    return (
        <div id="newRecipe" ref={addRef} className="modal">
            <div className="modal-content">
            <span onClick={onModalCancel} className="close">&times;</span>
            <h4>Add New Recipe</h4>
                <div className="input-field">
                <input id="add_name" type="text" className="validate"/>
                <label htmlFor="add_name">Name</label>
                </div>
                <div className="input-field">
                <input id="add_ingredients" type="text" className="validate"/>
                <label htmlFor="add_ingredients">Ingredients</label>
                </div>
            <div className="input-field">
                <input id="add_photo" type="text" className="validate"/>
                <label htmlFor="add_photo">Photo</label>
                </div>
            </div>
            <div className="modal-footer">
            <a onClick={() => {}} href="#!" className=" modal-action modal-close waves-effect waves-green btn-flat">Agree</a>
            </div>
        </div>
    )
});

export default AddRecipeModal;
